	private void testInternal(double gamma, double[][][] transitions, double[][][] rewards) throws Exception {
		MDP mdp = new MDPBuilder(transitions, rewards).build();
		
		log.info("Running Policy Iteration for original MDP ...");
		PolicyIteration pi = new PolicyIteration(mdp);
		pi.setGamma(gamma);
		pi.run();
		
		ValueIteration vi = new ValueIteration(mdp);
		vi.setGamma(gamma);
		vi.run();
		String res = getStateValuesWithDiscount(mdp, gamma);
		
		log.info("Running Reward shaping ...");
		updateMDP(mdp);
		log.info("Running Policy Iteration for modified MDP ...");
		pi = new PolicyIteration(mdp);
		pi.setGamma(0.99);
		pi.run();
		
		log.info(res);
		//0.0,0.783932224966973,0.662927463685071,0.0,0.0,0.9352185805434607,0.771952753600065,0.0,0.0,0.7839322249669733,0.6629274636850714,0.0
	}

	private String getStateValuesWithDiscount(MDP mdp, double gamma) {
		NumberFormat nf = NumberFormat.getNumberInstance(Locale.US);
		nf.setMinimumFractionDigits(10);
		nf.setMaximumFractionDigits(10);
		nf.setGroupingUsed(false);
		StringBuilder res = new StringBuilder();
		//mdp.getStates().forEach((state) -> res.append(nf.format(state.getValue())).append(", "));
		mdp.getStates().forEach((state) -> res.append(format(state.getValue())).append(", "));
		return res.toString();
	}

	private Object format(Double value) {
		//		NumberFormat nf = new DecimalFormat("0.###########E0", DecimalFormatSymbols.getInstance(Locale.US));
		//	
		//		if(value > 1E7) {
		//			return nf.format(value);
		//		}
		return value;
	}

	private void updateMDP(MDP mdp) {
		//ValueIteration vi = new ValueIteration(mdp);
		//vi.run();
		Map<State, Double> stateValues = new HashMap<>();
		mdp.getStates().forEach((state) -> stateValues.put(state, state.getValue()));
		mdp.getStateActions().values().forEach((stateActions) -> stateActions.forEach((stateAction) -> stateAction.getTransitions().keySet().forEach((transition) -> transition.setReward(transition.getReward() + (transition.getsPrime().getValue() - transition.getS().getValue())))));
	}
